import React from 'react';

function getWeatherIcon(wmoCode) {
  const icons = new Map([
    [[0], "â˜€ï¸"],
    [[1], "ðŸŒ¤"],
    [[2], "â›…ï¸"],
    [[3], "â˜ï¸"],
    [[45, 48], "ðŸŒ«"],
    [[51, 56, 61, 66, 80], "ðŸŒ¦"],
    [[53, 55, 63, 65, 57, 67, 81, 82], "ðŸŒ§"],
    [[71, 73, 75, 77, 85, 86], "ðŸŒ¨"],
    [[95], "ðŸŒ©"],
    [[96, 99], "â›ˆ"],
  ]);
  const arr = [...icons.keys()].find((key) => key.includes(wmoCode));
  if (!arr) return "NOT FOUND";
  return icons.get(arr);
}

function convertToFlag(countryCode) {
  const codePoints = countryCode
    .toUpperCase()
    .split("")
    .map((char) => 127397 + char.charCodeAt());
  return String.fromCodePoint(...codePoints);
}

function formatDay(dateStr) {
  return new Intl.DateTimeFormat("en", {
    weekday: "short",
  }).format(new Date(dateStr));
}


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      location: "lisbon", 
      isLoading: false, 
      displayLocation: "", 
      weather: {},
    };
    this.fetchWeather = this.fetchWeather.bind(this);
  }

  async fetchWeather() {
    try {
      this.setState({isLoading: false});


      // 1) Getting location (geocoding)
      const geoRes = await fetch(
        `https://geocoding-api.open-meteo.com/v1/search?name=${this.state.location}`
      );
      const geoData = await geoRes.json();
      console.log(geoData);

      if (!geoData.results) throw new Error("Location not found");

      const { latitude, longitude, timezone, name, country_code } =
        geoData.results.at(0);  

      this.setState({displayLocation: `${name} ${convertToFlag
          (country_code)}`, 
      });

      // 2) Getting actual weather
      const weatherRes = await fetch(
        `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&timezone=${timezone}&daily=weathercode,temperature_2m_max,temperature_2m_min`
      );
      const weatherData = await weatherRes.json();
      this.setState({ weather: weatherData.daily});
    } catch (err) {
      console.err(err);
    } finally {
      this.setState({ isLoading: false });
    }
  }

  render() {
    return <div className='app'>
      <h1>Classy Weather</h1>
      <div>
        <input type="text" 
        placeholder="Search from location..." 
        value={this.state.location}
        onChange={(e) => this.setState({ location: e.target.value })}/>
      </div>
      <button onClick={this.fetchWeather}>Get Weather</button>

      {this.state.isLoading && <p 
      className="loader">Loading...</p>}

      {this.state.weather.weathercode && <Weather 
      weather={this.state.weather} 
      location={this.state.displaylocation} />}
    </div>
  }
}

export default App;

class Weather extends React.Component {
  render() {
    const {
      temperature_2m_max: max,
      tempurature_2m_min: min,
      time: dates,
      weathercode: codes,
    } = this.props.weather;

    return (
    <div>
      <h2>Weather {this.props.location}</h2>
      <ul className="weather">
        {dates.map((date, i) => (
        <Day 
        date={date} 
        max={max.at(i)} 
        min={min.at(i)} 
        code={codes.at(i)}
        key={date}
        />
        ))}
      </ul>
    </div>
    );
  }
}

class Day extends React.Component {
  render() {
    const { date, max, min, code } = this.props;

    return (
    <li className="day">
      <span>{code}</span>
      <p>{date}</p>
      <p>
        {Math.floor(min)}&deg; &mdash;{Math.ceilmax}&deg;
      </p>
    </li>
    );
  }
}
